$(document).ready(function() {
    console.log( "ready!" );
});

function loadPage(obj){
	getMainContent($(obj).html());
}


function getMainContent(page){
	$.get("./generator.php?id="+page.toLowerCase()+"&content=menu",function(retorno){
		$("#sub-menu").html(retorno);
	});
	$.get("./generator.php?id="+page.toLowerCase()+"&content=content",function(retorno){
		$("#main-content").html(retorno);
	});
}

function getContent(menu, content){
	console.log("./generator.php?id=content&menu="+menu.toLowerCase()+"&content="+content.toLowerCase());
	console.log("erro abaixo");
	$.get("./generator.php?id=content&menu="+menu.toLowerCase()+"&content="+content.toLowerCase(),function(retorno){
		console.log(retorno);
		$("#main-content").html(retorno);
	});
}